with open('rosalind_hamm.txt','r') as file:
    f = file.read().splitlines()
    s = str(f[0])
    t = str(f[1])

#s = ''
#t = ''

def hamm(s,t):
    dH = ''
    for i in range(len(s)):
        if s[i] != t[i]:
            dH += '1'
    print(sum(map(int, dH)))

hamm(s, t)
            
